$(document).ready(function(){$(".date-picker").datepicker({autoclose:!0}),$("#sample_1").DataTable(),$(".repeater").repeater({show:function(){$(this).slideDown()},hide:function(e){confirm("Are you sure you want to delete this element?")&&$(this).slideUp(e)},isFirstItemUndeletable:!0})}),$(function(){$.fn.sortList=function(){var e=$(this),t=$("li",e).get();t.sort(function(e,t){var i=$(e).text().toUpperCase(),n=$(t).text().toUpperCase();return i<n?-1:1}),$.each(t,function(t,i){e.append(i)})},$("#list-pages").sortList()});